$(document)
    .on("click",
        ".navbar-toggle", function () {
            $toggle = $(this),
                1 == paperDashboard.misc.navbar_menu_visible ? ($("html").removeClass("nav-open"),
                    paperDashboard.misc.navbar_menu_visible = 0,
                    setTimeout(function () {
                        $toggle.removeClass("toggled"),
                            $("#bodyClick").remove()
                    }, 550)) : (setTimeout(function () {
                        $toggle.addClass("toggled")
                    }, 580),
                        div = '<div id="bodyClick"></div>',
                        $(div).appendTo("body").click(function () {
                            $("html").removeClass("nav-open"),
                                paperDashboard.misc.navbar_menu_visible = 0,
                                setTimeout(function () {
                                    $toggle.removeClass("toggled"), $("#bodyClick").remove()
                                }, 550)
                        }),
                        $("html").addClass("nav-open"),
                        paperDashboard.misc.navbar_menu_visible = 1)
        }),
    $(window).resize(),
    paperDashboard = {
        misc: { navbar_menu_visible: 0 },
        initMinimizeSidebar: function () {
            0 != $(".sidebar-mini").length && (sidebar_mini_active = !0),
                $("#minimizeSidebar").click(function () {
                    $(this);
                    1 == sidebar_mini_active ? ($("body").addClass("sidebar-mini"),
                         sidebar_mini_active = !0,
                         paperDashboard.showSidebarMessage("Sidebar mini activated...")) : ($("body").removeClass("sidebar-mini"),
                         sidebar_mini_active = !1, paperDashboard.showSidebarMessage("Sidebar mini deactivated..."));
                         var a = setInterval(function () { 
                             window.dispatchEvent(new Event("resize"))
                         }, 180);
                    setTimeout(function () {
                         clearInterval(a)
                    }, 1e3)
                })
        }
    };
